int pp(int x,int y)
{

	if (x==y) {ans++;return 0;}
	ll min=1000000001;
	int l,r;
	for (i=x;i<=y;i++)
	if (h[i]<min) min=h[i];
	if (min>=n) {ans=n;return 0;}
	for (i=x;i<=y;i++)
	h[i]-=min;
	ans+=min;
	l=x;
	while (h[l]==0) l++;
	r=l;
	while (r<=y)
	{
		while ((h[r+1]!=0) && (r+1<=y)) r++;
		if (l<=r) pp(l,r);
		l=r+1;
		while (h[l]==0) l++;
		r=l;
	}
	return 0;
}

