int solution(int start,int end,int num[])
{
    if(start==end)
        return min(num[start],1);
    if(start>end)
        return 0;
    else
    {
        int minu=INT_MAX;
        int j;
        for(int i=start;i<=end;i++)
        {
            if(minu>num[i])
            {
                minu=num[i];
                j=i;
            }
        }
        for(int i=start;i<=end;i++)
            num[i]-=minu;
        int result_a=solution(start,j-1,num);
        int result_b=solution(j+1,end,num);
        return min(result_a+result_b+minu,end-start+1);
    }
}





