#include<stdio.h>

void shellSort(int data[],int len)
{
	int m=(int)(log(len+1)/log(2));//计算hibbard增量的长度。 
	int i;
	int G[m];
	for(i=0;i<m;i++)
		G[i]=(int)pow(2,i+1)-1;//计算Hibbard增量
	for(i=m-1;i>=0;i--)
		insertionSort(data,len,G[i]);
}
void insertionSort(int data[],int len,int g)
{
	int i,j,v;
	for(i=g;i<len;i++)
	{
		v=data[i];
		for(j=i-g;j>=0&&data[j]>v;j=j-g)
			data[j+g]=data[j];
		data[j+g]=v;
	}
}
int main()
{
	int n,m;
	int a[200000],b[200000];
	int i,j;
	int left,right,mid;
	scanf("%d%d",&n,&m);
	for(i=0;i<n;i++)
		scanf("%d",&a[i]);
	for(j=0;j<m;j++)
		scanf("%d",&b[j]);
	shellSort(a,n);

	for(j=0;j<m;j++)
	{
		left=0;
		right=n-1;

		while(right>left)
		{
			mid=(left+right)/2;	
			if(a[mid]<b[j]+1)
				left=mid+1;
			else
				right=mid;	
		}
		if(n!=1)
			if(left<n-1)
				printf("%d ",left);
