{
	int T;
	scanf("%d", &T);
	double h, c, t;
	double aver;
	double all;
	double D_value;
	double min;
	int time, flag;
	while (T--)
	{
		flag = 1;
		aver = 0;
		time = 1;
		D_value = 0;
		scanf("%lf %lf %lf", &h, &c, &t);
		all = h + c;

		if (all / 2 == t)
		{
			printf("2\n");
			continue;
		}
		if (h == t)
		{
			printf("1\n");
			continue;
		}
		min = t - (all / 2);
		if (min < fabs(t - ((all * (time + 1.0) + h) / ((time + 1.0) * 2.0 + 1.0))))
		{
			printf("2\n");
			continue;
		}
		while (1)
		{
			aver = ((all * time + h) / (time * 2.0 + 1.0));
			D_value = fabs(t - aver);
			if (flag == 1)
				min = D_value;
			if (D_value > min)
			{
				time--;
				break;
			}
			min = D_value;
			time++;
			flag = 0;
		}
		printf("%d\n", time * 2 + 1);
	}
	return 0;
}

