#include <stdio.h>

int checking(int n)
{
    int i = 1,digit;
    while (n >= 1)
    {
        digit = n % 10;
        n = n / 10;
        if (digit % 2 == 0)
        {
            return i;
        }
        i++;
    }
    return -1;
}
int main()
{
    int t;
    scanf("%d", &t);
    int ans[t];
    for (int test = 0; test < t; test++)
    {
        int n;
        scanf("%d", &n);

        if((n%10)%2==0)
        {
            ans[test]=0;
            continue;
        }
        // reversing n
        int digit, reverse = 0;
        while (n >= 1)
        {
            digit = n % 10;
            n = n / 10;
            reverse = reverse * 10 + digit;
        }

        ans[test]=checking(reverse);
    }

    // printing output
    for (int test = 0; test < t; test++)
    {
        printf("%d\n", ans[test]);
    }
    return 0;
}

