    scanf("%d\n", &numChocs);

    int *chocs;
    chocs = (int *) malloc(sizeof(int) * numChocs);

    if (numChocs > 0)
        scanf("%d", chocs);

    for (int i = 1; i < numChocs; ++i) {
        scanf(" %d", chocs + i);
    }

    int *alice = chocs;
    int acount = 0;

    int *bob = chocs + numChocs - 1;
    int bcount = 0;
    bool bobStarted = FALSE;

    while (alice < bob) {
        int min = (*alice < *bob) ? *alice : *bob;

        *alice -= min;
        *bob -= min;
        if (*alice == 0) {
            ++alice;
            ++acount;
        }
        if (*bob == 0) {
            --bob;
            ++bcount;
            bobStarted = FALSE;
        }
        else
            bobStarted = TRUE;
    }

    if (*alice && !bobStarted) {
        ++acount;
        *alice = 0;
    }
    if (*bob) {
        ++bcount;
        *bob = 0;
    }

    printf("%d %d\n", acount, bcount);
}

