/* practice with Dukkha */
#include <stdio.h>
#include <string.h>

#define N	100
#define M	1000

int main() {
	static double dp[N * M + 1], aa[N * M + M + 2];
	int n, m, i, j, sum;
	double ans;

	scanf("%d%d", &n, &m);
	if (m == 1) {
		printf("1\n");
		return 0;
	}
	sum = 0;
	dp[0] = 1;
	for (i = 0; i < n; i++) {
		int x, j;
		double a;

		scanf("%d", &x), sum += x;
		memset(aa, 0, (n * m + m + 2) * sizeof *aa);
		for (j = 0; j <= n * m; j++) {
			double z = dp[j];

			if (z != 0) {
				aa[j + 1] += z;
				aa[j + m + 1] -= z;
				aa[j + x] -= z;
				aa[j + x + 1] += z;
			}
		}
		a = 0;
		for (j = 0; j <= n * m; j++) {
			a += aa[j];
			dp[j] = a / (m - 1);
		}
	}
	ans = 0;
	for (j = 0; j < sum; j++)
		ans += dp[j];
	ans = ans * (m - 1) + 1;
	printf("%f\n", ans);
	return 0;
}

