    int t;
    long long int sum=0;
    scanf("%d",&t);
    int a[200005]={0},top=(1),ss=0,b[200005]={0};
    while (t) {
        int n,num=0;
        scanf("%d",&n);
        if(n==1){
            int k,kk;
            scanf("%d %d",&kk,&k);
            kk=top<kk?top:kk;
            sum+=(k*kk);
            a[kk]+=k;
        }
        else if(n==2){
            int k;
            scanf("%d",&k);
            sum+=k;
            top++;
            b[top]=k;
        }
        else{
            if(top>0){
                ss+=a[top];
                sum-=(ss+b[top]);
                a[top-1]=a[top]
                a[top]=0;
                b[top]=0;
                top--;
            }
        }
        t--;
        printf("%lf\n",(double)sum/(double)top);
    }
    return 0;
}

