typedef struct{
	int ini;
	int fin;
}reservacion;
void quicksort(reservacion A[], int ini, int fin);
int main(){
	int n,i,max_orders,max;
	scanf("%d",&n);
	reservacion orders[n];

	for(i=0;i<n;i++){
		scanf("%d %d",&orders[i].ini,&orders[i].fin);
	}

	quicksort(orders,0,n-1);

	max_orders=1;
	i=1;
	max=orders[0].fin;

	while(i<n){
		if(orders[i].ini>max){
			max_orders++;
			max=orders[i].fin;
		}
		i++;
	}

	printf("%d",max_orders);
	return 0;
}



void quicksort(reservacion A[], int ini, int fin){
    int central = (ini + fin)/2;
    int pivote = A[central].ini;
    int i=ini,j=fin;
    reservacion aux;
    do{
        while(A[i].ini<pivote) i++;
        while(A[j].ini>pivote) j--;
        if(i<=j){
            aux = A[i];
            A[i]=A[j];
            A[j]=aux;
            i++;
            j--;
