void quicksort(reservacion A[], int ini, int fin){
    int central = (ini + fin)/2;
    int pivote = A[central].fin;
    int i=ini,j=fin;
    reservacion aux;
    do{
        while(A[i].fin<pivote)
			 i++;
        while(A[j].fin>pivote) 
			j--;
        if(i<=j){
            aux = A[i];
            A[i]=A[j];
            A[j]=aux;
            i++;
            j--;
        }
    }
    while(i<=j);
    if(ini<j){
        quicksort(A,ini,j);
    }
    if(fin>i){
        quicksort(A,i,fin);
    }
}

