	for (i = 0; i < total; ++i) {

		scanf("%d%d%d", &n, &a, &b);
		left = 0;
		right = n / 2;
		cnt[i] = n;
		if (a - b > 1) {
			output[i][0] = -1;
			continue;
		}
		output[i][left] = a;
		output[i][right] = b;
		for (j = b + 1; j <= n; ++j) {
			if (j == a) {
				continue;
			}
			output[i][++left] = j;
		}
		if (left >= n / 2) {
			output[i][0] = -1;
			continue;
		}
