		scanf("%d", &i), i--;
		gg[i] = 1;
	}
	for (i = 0; i < m; i++)
		scanf("%d%d", &ii[i], &jj[i]), ii[i]--, jj[i]--;
	memset(dsu, -1, n * sizeof *dsu);
	for (i = 0; i < m; i++)
		join(ii[i], jj[i]);
	for (i = 0; i < m; i++)
		kk[find(i)]++;
	i_ = -1;
	for (i = 0; i < n; i++)
		if (dsu[i] < 0 && kk[i_] < kk[i])
			i_ = i;
	x = 0;
	for (i = 0; i < n; i++) {
		j = find(i);
		if (j == i_ || !gg[j])
			xx[i] = 1, x++;
	}
	ans = x * (x - 1) / 2;
	for (i = 0; i < m; i++)
		if (xx[ii[i]] && xx[jj[i]])
			ans--;
	printf("%d\n", ans);
	return 0;
}

