#include <stdio.h>

int main() {
	int n, k, l, a, o, e, odd;

	scanf("%d%d", &n, &k), l = n - k;
	o = 0, e = 0;
	while (n--) {
		scanf("%d", &a);
		if (a % 2 == 0)
			e++;
		else
			o++;
	}
	if (o <= l / 2)
		odd = 0;
	else if (e <= l / 2)
		odd = k % 2;
	else if (l % 2 == 0)
		odd = 0;
	else if (e > l / 2 + 1 || o > l / 2 + 1)
		odd = 1;
	else	/* l = m * 2 + 1 && e == m + 1 && o == m + 1 */
		odd = k % 2;
	printf(odd ? "Stannis\n" : "Daenerys\n");
	return 0;
}

