#include<math.h>
#include<time.h>
#define N 1000

void swap(int *p,int *q)
{
    int temp;
    temp = *p;
    *p = *q;
    *q = temp;
}

int a[N];

int main()
{
    int m,n;
    while(~scanf("%d%d", &m, &n))
    {
        for(int i = 0;i < m;i ++)
        {
            scanf("%d", &a[i]);
            for(int j = 0; j < i; j ++)
            {
                if(a[i] > a[j])
                    swap(a + i, a + j);
            }
        }
        int max = a[m - 1] > n - a[0] ? a[m - 1] * 2 : n - a[0] * 2;
        for(int i = 0; i < m  -1; i ++)
        {
            max = a[i] - a[i + 1] > max ? a[i] - a[i + 1] : max;
        }
        printf("%.10lf\n",(double)max / 2);
    }
    return 0;
}

