int canbe(int dis)
{
	int i,j,max,tmp,delta;
	for(i=0;i<n;i++)
	{
		dp[i]=1;
		for(j=0;j<i;j++)
		{
			delta=num[i]-num[j];
			if(delta<0)delta=-delta;
			if(delta<=(i-j)*dis)
			{
				tmp=dp[j]+1;
				if(tmp>dp[i])dp[i]=tmp;
			}
		}
	}
	max=0;
	for(i=0;i<n;i++)if(dp[i]>max)max=dp[i];
	if(max>=n-k)return 1;
	else return 0;;
}
