#define N	100

int compare(const void *a, const void *b) {
	int *ia = (int *) a;
	int *ib = (int *) b;

	return *ia - *ib;
}

int main() {
	static int aa[N], bb[N];
	int n, m, h, i, j;
	long long sum;

	scanf("%d%d", &n, &m);
	sum = 0;
	for (i = 0; i < n; i++) {
		scanf("%d", &aa[i]);
		sum += aa[i];
	}
	for (j = 0; j < m; j++) {
		scanf("%d", &i);
		i--;
		bb[j] = aa[i];
		sum -= aa[i];
	}
	qsort(bb, m, sizeof *bb, compare);
	for (j = 0; j < m && sum >= bb[j]; j++)
		sum *= 2;
	for (h = m - 1; h >= j; h--)
		sum += sum >= bb[h] ? sum : bb[h];
	printf("%lld\n", sum);
	return 0;
}

