#include <stdlib.h>
#include <limits.h>
#include <stdio.h>

#define MAX_N (5000)

int xs[MAX_N];
int n;

int min_(int x, int y) { return x < y ? x : y; }

int max_(int x, int y) { return x > y ? x : y; }

int min(int l, int r, int h) {
  int acc;

  for ( acc=xs[l]-h; l < r; ++l )
    acc = min_(xs[l]-h, acc);
  return acc;
}

int solve(int l, int r, int h) {
  int i,j,mn,ret;
  int acc=0;

  mn = min(l, r, h);
  for ( i=j=l ; i < r; i = max_(i, j+1) ) {
    for ( ; i < r && xs[i]-h == mn; ++i );
    if ( i < r ) {
      for ( j=i+1; j < r && xs[j]-h > mn; ++j );
      acc += solve(i, j, mn);
    }
  }
  ret = min_(r-l, mn + acc);
  return ret;
}

int main() {
  int i;

  scanf("%d", &n);
  for ( i=0; i < n; ++i )
    scanf("%d", xs+i);
  printf("%d\n", solve(0, n, 0));
  return EXIT_SUCCESS;
}

