{
  int count = 0;
  int i = 0;

  while (a[0] < n && a[1] < n)
  {
    a[i % 2] += a[(i + 1) % 2];
    count++;
    i++;
  }
  if (a[0] == n || a[1] == n)
  {
    return count + 1;
  }
  else
  {
