#include <stdio.h>
#define MAX 100100
#define __L (p<<1), L, L+R>>1
#define __R (p<<1)+1, (L+R>>1)+1, R
typedef struct {int d, r;} node;
node seg[MAX<<2];
int n,q,l,r,i,A[MAX];
node func(node x, node y){
	if(!(x.d&&y.d))   return (node){0,0};
	if(x.d < y.d) 		return (node){y.d%x.d?0:x.d, x.r};
	if(x.d > y.d) 		return (node){x.d%y.d?0:y.d, y.r};
	if(x.d == y.d)		return (node){x.d, 			x.r+y.r};
}
node build(int p, int L, int R){
	if(L==R) return seg[p]=(node){A[L], 1};
	node x = build(__L);
	node y = build(__R);
	return seg[p]=func(x,y);
}
node RQ(int p, int L, int R, int i, int j){
	if(i>R || j<L) 		return (node){-1,0};
	if(L>=i && R<=j) 	return seg[p];
	node x = RQ(__L, i, j);
	node y = RQ(__R, i, j);
	if(x.d==-1) return y;
	if(y.d==-1) return x;
	return func(x,y);
}
int main(){
	for(scanf("%i",&n);i<n;i++)
			scanf("%i",&A[i]);
	build(1, 0, n-1);
	for(scanf("%i",&q);q--;){
		scanf("%i%i",&l,&r);
		node res = RQ(1, 0,n-1, l-1, r-1);
		printf("%i\n", res.d?r-l+1-res.r:r-l+1);
	}
}

