long jump_right(long right, long jumps) {
    return right * jumps;
}

long jump_left(long left, long jumps) {
    return left * jumps * -1;
}

long jump(long right, long left, long jumps) {
    if (jumps % 2 == 0) {   // even case
        return jump_right(right, jumps/2) + jump_left(left, jumps/2); 
    }
    else {    // odd case
        return jump_right(right, jumps/2) + jump_left(left, jumps/2) + right;
    }
}

