#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main(void) {
	long long int n, m;
	scanf("%lld%lld", &n, &m);

	long long int sum = 0;
	while (m--) {
		long long int x, d;
		scanf("%lld%lld", &x, &d);

		sum += (long long int) x*n;

		if (d > 0) {
			sum += (long long int) d*((n-1) * n)/2;
		} else {
			int l = (n-1)/2, r = n/2;
			sum += (long long int) d*((l+1) * l)/2;
			sum += (long long int) d*((r+1) * r)/2;
		}
	}

	printf("%.9f\n", (double) sum / n);
}

